#[smashline::fighter_frame_callback]
fn per_frame(fighter: &mut L2CFighterCommon) {
    unsafe {
        let lua_state = fighter.lua_state_agent;
        let module_accessor = smash::app::sv_system::battle_object_module_accessor(lua_state);
        let fighter_kind = smash::app::utility::get_kind(module_accessor);
        skyline::nn::ro::LookupSymbol(
            &mut FIGHTER_MANAGER_ADDR,
            "_ZN3lib9SingletonIN3app14FighterManagerEE\
      9instance_E\u{0}"
                .as_bytes()
                .as_ptr(), );
        let FIGHTER_MANAGER = *(FIGHTER_MANAGER_ADDR as *mut *mut smash::app::FighterManager);
        let mut ENTRY_ID = WorkModule::get_int(module_accessor, *FIGHTER_INSTANCE_WORK_ID_INT_ENTRY_ID);
        let status_kind = StatusModule::status_kind(module_accessor);
        if fighter_kind == *FIGHTER_KIND_ROY{
            if [*FIGHTER_ROY_STATUS_KIND_SPECIAL_N_END2, *FIGHTER_ROY_STATUS_KIND_SPECIAL_N_END3, *FIGHTER_ROY_STATUS_KIND_SPECIAL_N_END, *FIGHTER_ROY_STATUS_KIND_SPECIAL_N_LOOP]
                .contains(&status_kind){
                StatusModule::change_status_request_from_script(module_accessor, *FIGHTER_ROY_STATUS_KIND_SPECIAL_N_END_MAX, true);
            }
            if StatusModule::prev_status_kind(module_accessor, 0) == *FIGHTER_ROY_STATUS_KIND_SPECIAL_LW_HIT{
                FighterManager::set_position_lock(FIGHTER_MANAGER, smash::app::FighterEntryID(ENTRY_ID), false);
                CameraModule::set_camera_type(module_accessor, 1);
                HitModule::set_whole(module_accessor, smash::app::HitStatus(*HIT_STATUS_NORMAL), 0);
                //SlowModule::clear_whole();
                acmd!(lua_state, {
                    SLOW_OPPONENT(0, 0)
                CANCEL_FILL_SCREEN(0, 5)
                });
            }
            if status_kind == *FIGHTER_ROY_STATUS_KIND_SPECIAL_LW_HIT{
                HitModule::set_whole(module_accessor, smash::app::HitStatus(*HIT_STATUS_XLU), 0);
            }
            if status_kind == *FIGHTER_ROY_STATUS_KIND_SPECIAL_N_END_MAX{
                if ControlModule::check_button_trigger(module_accessor, *CONTROL_PAD_BUTTON_SPECIAL) && MotionModule::frame(module_accessor) < 30.0{
                    AttackModule::set_power_up(module_accessor, MotionModule::frame(module_accessor) * 1.8867924528301887 / 100.0);
                    MotionModule::set_rate(module_accessor, 35.0 - MotionModule::frame(module_accessor) - 1.0);
                }
                if MotionModule::frame(module_accessor) < 53.0{
                    CameraModule::req_quake(module_accessor, *CAMERA_QUAKE_KIND_M);
                }
            }
        }
        else{
            STOP_COUNT_FLAG = false;
            FRAME_COUNTER = 0.0;
        }
    }
}
